import React from 'react'
import styled from 'styled-components';

const Container = styled.div`
height: 100%;
background: #f6f6f6;
`;

const ContextMenu = styled.div`
width: 200px;
height: auto;
box-shadow: 0 0 20px 0 #ccc;
position: absolute;
display: block;
top: 312px;
left: 62px;

`;

const UlContextMenu = styled.ul`
list-style: none;
padding: 5px 0px 5px 0px;
`;

const ListContextMenu = styled.div`
padding: 10px 5px 10px 5px;
border-left: 4px solid transparent;
cursor: pointer;
`;


export default class CustomMenu extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            displayMenu: false ,
            clickX:null,
            clickY:null
        }
    }

        componentDidMount() {
        document.addEventListener('contextmenu', this._handleContextMenu);
        document.addEventListener('click', this._handleClick);
       // document.addEventListener('scroll', this._handleScroll);
    };

    componentWillUnmount() {
      document.removeEventListener('contextmenu', this._handleContextMenu);
      document.removeEventListener('click', this._handleClick);
      //document.removeEventListener('scroll', this._handleScroll);
    }

        _handleContextMenu = (event) => {
        event.preventDefault();
        var visibility = false;
        if(this.props.node.type === 'folder'){
             visibility = true;
        }
        else if(this.props.node.type === 'file'){
            visibility=false;
        }


        this.setState({ visible: visibility,
            clickX: this.props.clientX,
            clickY: this.props.clientY
        });


        var contextMenu = document.getElementById('contextMenu');
        contextMenu.style.display =  (visibility) ? 'block' : 'none';
        contextMenu.style.left = event.clientX + 'px';
        contextMenu.style.top = event.clientY + 'px';
    };

        _handleClick = (event) => {
                // const { visible } = this.state.displayMenu;
                // const wasOutside = !(event.target.contains === this.root);

                // if (wasOutside && visible) {
                //     this.setState({ visible: false,clickX:null, clickY:null });
                //     document.removeEventListener('contextmenu', this._handleContextMenu);
                //     document.removeEventListener('click', this._handleClick);
                //     //document.removeEventListener('scroll', this._handleScroll);
                // };
                contextMenu.style.display = 'none';
            };

    render() {
        return (
            <Container  oncontextmenu="return showContextMenu(event);">
                <ContextMenu id="contextMenu">
                    <UlContextMenu>
                        <ListContextMenu>Input Text:
                    <input type='text'></input>
                        </ListContextMenu>
                        <ListContextMenu>Generate</ListContextMenu>
                        {/* <li class="separator"></li> */}
                        <ListContextMenu>Delete</ListContextMenu>
                    </UlContextMenu>
                </ContextMenu>
            </Container>
        );
    }
}

