import React, { Component } from 'react';
import values from 'lodash/values';
import PropTypes from 'prop-types';

import TreeNodeNew from './TreeNodeNew';
//import ClickHandler from './ClickHandler';
import { Menu, Item, Separator, Submenu, MenuProvider } from 'react-contexify';
//import 'react-contexify/dist/ReactContexify.min.css';
import CustomMenu from './CustomMenu';
import ContextMenuNew from './ContextMenuNew';

import { withTracker } from 'meteor/react-meteor-data';

import { Factories } from '../api/factories.js';

const data = {
  '/root': {
    path: '/root',
    type: 'folder',
    isRoot: true,
    children: ['/root/david', '/root/jslancer'],
  },
  '/root/david': {
    path: '/root/david',
    type: 'folder',
    children: ['/root/david/1', '/root/david/2', '/root/david/3'],
  },
  '/root/david/1': {
    path: '/root/david/1',
    type: 'file'
  },
  '/root/david/2': {
    path: '/root/david/2',
    type: 'file'
  },
  '/root/david/3': {
    path: '/root/david/3',
    type: 'file'
  },
  '/root/jslancer': {
    path: '/root/jslancer',
    type: 'folder',
    children: ['/root/jslancer/132', '/root/jslancer/12'],
  },
  '/root/jslancer/132': {
    path: '/root/jslancer/132',
    type: 'file'
  },
  '/root/jslancer/12': {
    path: '/root/jslancer/12',
    type: 'file'
  },
};

const onClick = ({ event, props }) => {
  console.log(event, props);
  console.log(props);
}
// create your menu first
const MyAwesomeMenu = (props) => {
  const { node } = props;
  return (

    <Menu node={node} id='menu_id' style={{ border: '1px solid purple', display: 'inline-block' }}>
      <Item node={node} onClick={(event, node) => onClick(event, node)}>Lorem</Item>
      <Item onClick={onClick}>Ipsum</Item>
      <Separator />
      <Item disabled>Dolor</Item>
      <Separator />
      <Submenu label="Foobar">
        <Item onClick={onClick}>Foo</Item>
        <Item onClick={onClick}>Bar</Item>
      </Submenu>
    </Menu>
  )
};

export  class Tree extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nodes: {},
      // clientX: undefined,
      // clientY: undefined,
      // isClicked: false
      // displayMenu: false,
      // clientX: 0,
      // clientY: 0,
      // nodeType: ""
    }
    // this.setMenuProperties = this.setMenuProperties.bind(this);
  }

  // state = {
  //   nodes: data,
  // };

  componentDidMount() {
    this.setState = ({
      nodes: this.props.factories
    })
  }

  getRootNodes = () => {
    //const { nodes } = this.state;
    const { nodes } = this.props.factories;
    //const result = values(this.props.factories).filter(node => node.isRoot === true);

    // var factories ={};
    // for(var i = 0; i< this.props.factories.length; i++){
    //   factories
    // }

    this.setState =({
      nodes: this.props.factories
    })
    const result = values(this.props.factories).filter(node => node.isRoot);
    return result;
  }

  getChildNodes = (node) => {
    this.setState = ({
      nodes: this.props.factories
    })
    const { nodes } = this.setState.nodes;
    if (!node.children) return [];
    const result= node.children.map(path => nodes[path].path);
    return result;
  }

  onToggle = (node) => {
    const { nodes } = this.state;
    nodes[node.path].isOpen = !node.isOpen;
    this.setState({ nodes });
  }

  onNodeSelect = node => {
    const { onSelect } = this.props;
    this.setState = { nodeType: node.type }
    //onSelect(node);
    return node;
  }


  setMenuProperties = (event, node) => {
    const { nodes } = this.state;
    const clientX = event.clientX;
    const clientY = event.clientY;
    nodes[node.path].isClicked = true;
    this.setState = {
      nodes: nodes,
      clientX: clientX,
      clientY: clientY,
      isClicked: true,
      nodeType: node.type

    };
    return state;
    //  this.setState({
    //      clicked: true,
    //      displayMenu: true,
    //      clientX: event.clientX,
    //      clientY: event.clientY,
    //      nodeType: node.type,
    //      nodeName: getNodeLabel(node)
    //  });
  }



  // setMenuProperties = (event, node) => {
  //   //if(this.state.displayMenu === true){
  //   this.setState=({
  //     displayMenu: true,
  //     clientX: event.clientX,
  //     clientY: event.clientY,
  //     nodeType: node.type
  //   });
  // //}
  //   console.log("event" + event);
  //  // return false;
  // }

  render() {
    const rootNodes = this.getRootNodes();
    return (
      <React.Fragment>

        {


          rootNodes.map(node => (

            <div>
              {/* <div onContextMenu={this.setMenuProperties(event, this.onNodeSelect(node))}> */}
                <TreeNodeNew
                  // onClick ={this.setMenuProperties(event, this.onNodeSelect(rootNodes[0]))}
                  displayMenu={this.state.displayMenu}
                  node={node}
                  getChildNodes={this.getChildNodes}
                  onToggle={this.onToggle}
                  onNodeSelect={this.onNodeSelect}
                  setMenuProperties={this.setMenuProperties}


                />


                {/* <CustomMenu
                  //node={node}
                  // nodeType={this.state.nodeType}
                  // onNodeSelect={this.onNodeSelect}
                  // getChildNodes={this.getChildNodes}
                   clientX = 'undefined'
                   clientY = 'undefined'
                   nodeType='folder'
                />  */}

                <ContextMenuNew />

              </div>


            // </div>




          ))}
        {/* <div>

              <CustomMenu
                //node={node}
                nodeType={this.state.nodeType}
                onNodeSelect={this.onNodeSelect}
                getChildNodes={this.getChildNodes}
                clientX={this.state.clientX}
                clientY={this.state.clientY}
                type={this.type} /> 

          </div> */}
        {/* </div> */}
      </React.Fragment>

    )
  }
}

Tree.propTypes = {
  onSelect: PropTypes.func.isRequired,
};
export default withTracker(() => {
  return {
    factories: Factories.find({}).fetch(),
  };
})(Tree);
