export  class Tree extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nodes: {},
      // clientX: undefined,
      // clientY: undefined,
      // isClicked: false
      // displayMenu: false,
      // clientX: 0,
      // clientY: 0,
      // nodeType: ""
    }
    // this.setMenuProperties = this.setMenuProperties.bind(this);
  }

  // state = {
  //   nodes: data,
  // };

  componentDidMount() {
    this.setState = ({
      nodes: this.props.factories
    })
    Meteor.call('factories.insert', data._id);
  }

  deleteFactory = () =>
  {
    Meteor.call('tasks.remove', this.props.task._id);
  }

  getRootNodes = () => {
    //const { nodes } = this.state;
    const { nodes } = this.props.factories;
    //const result = values(this.props.factories).filter(node => node.isRoot === true);

    // var factories ={};
    // for(var i = 0; i< this.props.factories.length; i++){
    //   factories
    // }

    this.setState =({
      nodes: this.props.factories
    })
    const result = values(this.props.factories).filter(node => node.isRoot);
    return result;
  }

  getChildNodes = (node) => {
    this.setState = ({
      nodes: this.props.factories
    })
    const  nodes  = this.setState.nodes;
    if (!node.children) return [];
    //const result= node.children.map(path => nodes[path].path);
    else
    //const childrenCount = node.children.length;
    var result= [];
    node.children.forEach((child, index) => 
    {
      //return arr[index] =  * 2num;
      for(var i = 0; i<nodes.length; i++)
      {
        console.log("i :" + i);
        if(nodes[i].path == child){
          result.push(nodes[i]);
        }
      }
  });
    return result;
  }

  onToggle = (node) => {
    const { nodes } = this.state;
    nodes[node.path].isOpen = !node.isOpen;
    this.setState({ nodes });
  }

  onNodeSelect = node => {
    const { onSelect } = this.props;
    this.setState = { nodeType: node.type }
    //onSelect(node);
    return node;
  }


  setMenuProperties = (event, node) => {
    const { nodes } = this.state;
    const clientX = event.clientX;
    const clientY = event.clientY;
    nodes[node.path].isClicked = true;
    this.setState = {
      nodes: nodes,
      clientX: clientX,
      clientY: clientY,
      isClicked: true,
      nodeType: node.type

    };
    return state;
    //  this.setState({
    //      clicked: true,
    //      displayMenu: true,
    //      clientX: event.clientX,
    //      clientY: event.clientY,
    //      nodeType: node.type,
    //      nodeName: getNodeLabel(node)
    //  });
  }



  // setMenuProperties = (event, node) => {
  //   //if(this.state.displayMenu === true){
  //   this.setState=({
  //     displayMenu: true,
  //     clientX: event.clientX,
  //     clientY: event.clientY,
  //     nodeType: node.type
  //   });
  // //}
  //   console.log("event" + event);
  //  // return false;
  // }

  render() {
    const rootNodes = this.getRootNodes();
    return (
      <React.Fragment>

        {


          rootNodes.map(node => (

            <div>
              {/* <div onContextMenu={this.setMenuProperties(event, this.onNodeSelect(node))}> */}
                <TreeNodeNew
                  // onClick ={this.setMenuProperties(event, this.onNodeSelect(rootNodes[0]))}
                  displayMenu={this.state.displayMenu}
                  node={node}
                  getChildNodes={this.getChildNodes}
                  onToggle={this.onToggle}
                  onNodeSelect={this.onNodeSelect}
                  setMenuProperties={this.setMenuProperties}


                />


                {/* <CustomMenu
                  //node={node}
                  // nodeType={this.state.nodeType}
                  // onNodeSelect={this.onNodeSelect}
                  // getChildNodes={this.getChildNodes}
                   clientX = 'undefined'
                   clientY = 'undefined'
                   nodeType='folder'
                />  */}

                <ContextMenuNew />

              </div>


            // </div>




          ))}
        {/* <div>

              <CustomMenu
                //node={node}
                nodeType={this.state.nodeType}
                onNodeSelect={this.onNodeSelect}
                getChildNodes={this.getChildNodes}
                clientX={this.state.clientX}
                clientY={this.state.clientY}
                type={this.type} /> 

          </div> */}
        {/* </div> */}
      </React.Fragment>

    )
  }
}

Tree.propTypes = {
  onSelect: PropTypes.func.isRequired,
};
export default withTracker(() => {
  return {
    factories: Factories.find({}).fetch(),
    items: Factories.find({}).fetch(),
  };

})(Tree);
