import React, { Component } from 'react';
import values from 'lodash/values';
import PropTypes from 'prop-types';

import TreeNode from './TreeNode';
//import ClickHandler from './ClickHandler';
import { Menu, Item, Separator, Submenu, MenuProvider } from 'react-contexify';
//import 'react-contexify/dist/ReactContexify.min.css';
import CustomMenu from '../ui/CustomMenu';

const data = {
  '/root': {
    path: '/root',
    type: 'folder',
    isRoot: true,
    children: ['/root/david', '/root/jslancer'],
  },
  '/root/david': {
    path: '/root/david',
    type: 'folder',
    children: ['/root/david/1', '/root/david/2','/root/david/3'],
  },
  '/root/david/1': {
    path: '/root/david/1',
    type: 'file'
  },
  '/root/david/2': {
    path: '/root/david/2',
    type: 'file'
  },
  '/root/david/3': {
    path: '/root/david/3',
    type: 'file'
  },
  '/root/jslancer': {
    path: '/root/jslancer',
    type: 'folder',
    children: ['/root/jslancer/132', '/root/jslancer/12'],
  },
  '/root/jslancer/132': {
    path: '/root/jslancer/132',
    type: 'file'
  },
  '/root/jslancer/12': {
    path: '/root/jslancer/12',
    type: 'file'
  },
};

const onClick = ({ event, props }) => {console.log(event,props);
console.log(props);}
// create your menu first
const MyAwesomeMenu = (props) => {
    const {node} = props;
    return(

    <Menu  node = {node} id='menu_id' style={{ border: '1px solid purple', display: 'inline-block' }}>
       <Item  node = {node}  onClick={(event,node) => onClick(event,node)}>Lorem</Item>
       <Item onClick={onClick}>Ipsum</Item>
       <Separator />
       <Item disabled>Dolor</Item>
       <Separator />
       <Submenu label="Foobar">
        <Item onClick={onClick}>Foo</Item>
        <Item onClick={onClick}>Bar</Item>
       </Submenu>
    </Menu>
)};

export default class Tree extends Component {
    constructor(props){
        super(props);
        this.state = {
            nodes: data,
            displayMenu: false,
            clientX: 0,
            clientY: 0
        }
    }

//   state = {
//     nodes: data,
//   };

  getRootNodes = () => {
    const { nodes } = this.state;
    return values(nodes).filter(node => node.isRoot === true);
  }

  getChildNodes = (node) => {
    const { nodes } = this.state;
    if (!node.children) return [];
    return node.children.map(path => nodes[path]);
  }  

  onToggle = (node) => {
    const { nodes } = this.state;
    nodes[node.path].isOpen = !node.isOpen;
    this.setState({ nodes });
  }

  onNodeSelect = node => {
    const { onSelect } = this.props;
    onSelect(node);
  }

  setMenuProperties = (event) =>{
      this.setState({
          displayMenu: true,
          clientX: event.clientX,
          clientY: event.clientY
      });
      console.log("event" +event);

  }

  render() {
    const rootNodes = this.getRootNodes();
    return (


        rootNodes.map(node => (
             <div onContextMenu = {this.setMenuProperties}>
          <TreeNode 
            displayMenu={this.state.displayMenu}
            node={node}
            getChildNodes={this.getChildNodes}
            onToggle={this.onToggle}
            onNodeSelect={this.onNodeSelect}
          />
         { (this.state.displayMenu === true  ?
                <CustomMenu displayMenu={this.state.displayMenu} 
                clientX = {this.state.clientX}
                clientY ={this.state.clientY} 
                type = {this.type}/>
               : null)}

          </div>


        ))


    )
  }
}

Tree.propTypes = {
  onSelect: PropTypes.func.isRequired,
};
