import React from 'react';
import { FaFile, FaFolder, FaFolderOpen, FaChevronDown, FaChevronRight } from 'react-icons/fa';
import styled from 'styled-components';
import last from 'lodash/last';
import PropTypes from 'prop-types';
import { Menu, Item, Separator, Submenu, MenuProvider } from 'react-contexify';



const onClick = ({ event, props }) => {
    console.log(event, props);
    console.log(props);
}
// create your menu first
const MyAwesomeMenu = (props) => {
    const { node } = props;
    return (

        <Menu node={node} id='menu_id' style={{ border: '1px solid purple', display: 'inline-block' }}>
            <Item node={node} onClick={(event, node) => onClick(event, node)}>Lorem</Item>
            <Item onClick={onClick}>Ipsum</Item>
            <Separator />
            <Item disabled>Dolor</Item>
            <Separator />
            <Submenu label="Foobar">
                <Item onClick={onClick}>Foo</Item>
                <Item onClick={onClick}>Bar</Item>
            </Submenu>
        </Menu>
    )
};

const getPaddingLeft = (level, type) => {
    let paddingLeft = level * 20;
    if (type === 'file') paddingLeft += 20;
    return paddingLeft;
}

// const StyledTreeNode = styled.div`
//   display: flex;
//   flex-direction: row;
//   align-items: center;
//   padding: 5px 8px;
//   padding-left: ${props => getPaddingLeft(props.level, props.type)}px;
//   border-left: ${props => (props.level > 1 ? 2 : 0)}px solid rgba(128, 4, 77, 0.3);


//   &:hover {
//     background: lightgray;
//   }
// `;

const StyledTreeNode = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 5px 8px;
  margin-left: ${props => getPaddingLeft(props.level, props.type)}px;
  border-left: ${props => (props.level > 1 ? 2 : 0)}px solid rgba(128, 4, 77, 0.3);


  &:hover {
    background: lightgray;
  }
`;

const NodeIcon = styled.div`
  font-size: 12px;
  margin-right: ${props => props.marginRight ? props.marginRight : 5}px;
`;

const getNodeLabel = (node) => last(node.path.split('/'));

const TreeNode = (props) => {
    const { node, getChildNodes, level, onToggle, onNodeSelect,displayMenu } = props;

    return (
        <React.Fragment>
            <StyledTreeNode level={level} type={node.type}>
                <NodeIcon onClick={() => onToggle(node)}>
                    {node.type === 'folder' && (node.isOpen ? <FaChevronDown /> : <FaChevronRight />)}
                </NodeIcon>

                <NodeIcon marginRight={10}>
                    {node.type === 'file' && <FaFile />}
                    {node.type === 'folder' && node.isOpen === true && <FaFolderOpen />}
                    {node.type === 'folder' && !node.isOpen && <FaFolder />}
                </NodeIcon>


                <div role="button" onClick={() => onNodeSelect(node)}
                    contenteditable="true" margin-right="20px">
                    {getNodeLabel(node)}
                </div>
                {node.type === 'folder' ? <div margin-left="50px">Range</div> : ""}

            </StyledTreeNode>
            {node.isOpen && getChildNodes(node).map(childNode => (

                <div>
                    <TreeNode
                        {...props}
                        node={childNode}
                        level={level + 1}
                    />

                    {{displayMenu} && node.type == "folder" ? (
                        <CustomMenu  ></CustomMenu>
                      ) : null}

               </div>

            ))}

        </React.Fragment>
    );
}

TreeNode.propTypes = {
    node: PropTypes.object.isRequired,
    getChildNodes: PropTypes.func.isRequired,
    level: PropTypes.number.isRequired,
    onToggle: PropTypes.func.isRequired,
    onNodeSelect: PropTypes.func.isRequired,
};

TreeNode.defaultProps = {
    level: 0,
};

export default TreeNode;
