import React from 'react';
import { FaFile, FaFolder, FaFolderOpen, FaChevronDown, FaChevronRight } from 'react-icons/fa';
import styled from 'styled-components';
import last from 'lodash/last';
import PropTypes from 'prop-types';
import { Menu, Item, Separator, Submenu, MenuProvider } from 'react-contexify';
import CustomMenu from './CustomMenu';

const getPaddingLeft = (level, type) => {
    let paddingLeft = level * 20;
    if (type === 'file') paddingLeft += 20;
    return paddingLeft;
}

const StyledTreeNode = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 5px 8px;
  margin-left: ${props => getPaddingLeft(props.level, props.type)}px;
  border-left: ${props => (props.level > 1 ? 2 : 0)}px solid rgba(128, 4, 77, 0.3);


  &:hover {
    background: lightgray;
  }
`;

const NodeIcon = styled.div`
  font-size: 12px;
  margin-right: ${props => props.marginRight ? props.marginRight : 5}px;
`;

var i =0;
const getNodeLabel = (node) => last(node.path.split('/'));
export default class TreeNodeClass extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            clicked: false,
            displayMenu: false,
            clientX: 0,
            clientY: 0,
            nodeType: "",
            nodeName: ""
        }
        this.setMenuProperties = this.setMenuProperties.bind(this);
        const { node, getChildNodes, level, onToggle, onNodeSelect } = props;
    }


    setMenuProperties = (event, node) => {
       // const i = 0;

       if (event.stopPropagation){
       event.stopPropagation();
   }
       if(event.path[0].innerText == getNodeLabel(node)){
           console.log("True");
           console.log("event data: "+event.path[0].innerText);
           console.log("label :"+getNodeLabel(node))
        this.setState({
            clicked: true,
            displayMenu: true,
            clientX: event.clientX,
            clientY: event.clientY,
            nodeType: node.type,
            nodeName: getNodeLabel(node)
        },() => console.log("InNodeType :" +this.state.nodeType + "InNode :"+ this.state.nodeName ));
    }

        console.log("event" + event);
        return false;
        //console.log("displayMenu" + displayMenu);
    }
    render() {
        //const { node, getChildNodes, level, onToggle, onNodeSelect } = props;
        return (
            <React.Fragment>
                <div onContextMenu={() => this.props.setMenuProperties(event, this.props.onNodeSelect(this.props.node))}>
                    <StyledTreeNode level={this.props.level} type={this.props.node.type}>
                        {/* <NodeIcon onClick={() => onToggle(node)}>
                    {node.type === 'folder' && (node.isOpen ? <FaChevronDown /> : <FaChevronRight />)}
                </NodeIcon>

                <NodeIcon marginRight={10}>
                    {node.type === 'file' && <FaFile />}
                    {node.type === 'folder' && node.isOpen === true && <FaFolderOpen />}
                    {node.type === 'folder' && !node.isOpen && <FaFolder />}
                </NodeIcon> */}

                        <div
                            // role="button" onClick={() => this.props.onNodeSelect(this.props.node)}
                            contentEditable="true" margin-right="20px">
                            {getNodeLabel(this.props.node)}
                        </div>
                        {/* {this.props.node.type === 'folder' ? <div margin-left="50px">Range</div> : ""} */}


                        {/* {this.props.node.type === 'folder' ?
                <div onContextMenu = {(event) => setMenuProperties(event)}> </div>: ""} */}
                        {/* <div>
                            {this.state.displayMenu === true && this.props.node.type === 'folder'  && this.state.clicked?
                                <CustomMenu 
                                node ={this.props.node}
                                type = {this.props.node.type}
                                clientX = {this.state.clientX}
                                clientY ={this.state.clientY}
                                displayMenu = {this.state.displayMenu} />
                                : null}
                        </div> */}


                    </StyledTreeNode>
                {/* </div> */}
                {/* <div onContextMenu={this.setMenuProperties}> */}
                {this.props.getChildNodes(this.props.node).map(childNode => (
                    <div>
                        {/* <div onContextMenu={(event, childNode) => this.setMenuProperties(event, childNode)}></div>  */}
                        <TreeNodeClass
                            // onContextMenu={(event, childNode) => this.setMenuProperties(event, childNode)}
                            {...this.props}
                            node={childNode}
                            level={this.props.level + 1}
                        />
                    </div>

                ))}
                <div>
                    {this.state.nodeType === 'folder'  ?
                        <CustomMenu
                            nodeName={this.state.nodeName}
                            nodeType={this.state.nodeType}
                            clicked={this.state.clicked}
                            node={this.props.node}
                            type={this.props.node.type}
                            clientX={this.state.clientX}
                            clientY={this.state.clientY}
                            displayMenu={this.state.displayMenu} />
                        : null}
                </div>
                </div>



            </React.Fragment>
        );
    }

}
TreeNodeClass.propTypes = {
    node: PropTypes.object.isRequired,
    getChildNodes: PropTypes.func.isRequired,
    level: PropTypes.number.isRequired,
    onToggle: PropTypes.func.isRequired,
    onNodeSelect: PropTypes.func.isRequired,
    setMenuProperties: PropTypes.func.isRequired,
};

TreeNodeClass.defaultProps = {
    level: 0,
};
