export default class ContextMenu extends React.Component {

    constructor(props){
        super(props);
        this.state={
            visible: false,
            clickX:null,
            clickY:null
        }
        document.addEventListener('contextmenu', this._handleContextMenu);
        document.addEventListener('click', this._handleClick);
       // document.addEventListener('scroll', this._handleScroll);
    }

    // componentDidMount() {
    //     document.addEventListener('contextmenu', this._handleContextMenu);
    //     document.addEventListener('click', this._handleClick);
    //     document.addEventListener('scroll', this._handleScroll);
    // };

    // componentWillUnmount() {
    //   document.removeEventListener('contextmenu', this._handleContextMenu);
    //   document.removeEventListener('click', this._handleClick);
    //   document.removeEventListener('scroll', this._handleScroll);
    // }

    _handleContextMenu = (event) => {
        event.preventDefault();

        this.setState({ visible: true,
            clickX: this.props.clientX,
            clickY: this.props.clientY
        });

        const clickX = this.props.clientX;
        const clickY = this.props.clientY;
        const screenW = window.innerWidth;
        const screenH = window.innerHeight;
        // const rootW = this.root.offsetWidth;
        // const rootH = this.root.offsetHeight;

        const right = (screenW - clickX) > 0;
        const left = !right;
        const top = (screenH - clickY) > 0;
        const bottom = !top;

        // if (right) {
        //     this.root.style.left = `${clickX + 5}px`;
        // }

        // if (left) {
        //     this.root.style.left = `${clickX - rootW - 5}px`;
        // }

        // if (top) {
        //     this.root.style.top = `${clickY + 5}px`;
        // }

        // if (bottom) {
        //     this.root.style.top = `${clickY - rootH - 5}px`;
        // }
    };

    _handleClick = (event) => {
        const { visible } = this.state;
        const wasOutside = !(event.target.contains === this.root);

        if (wasOutside && visible) {
            this.setState({ visible: false,clickX:null, clickY:null });
            document.removeEventListener('contextmenu', this._handleContextMenu);
            document.removeEventListener('click', this._handleClick);
            //document.removeEventListener('scroll', this._handleScroll);
        };
    };

    _handleScroll = () => {
        const { visible } = this.state;

        if (visible) this.setState({ visible: false,clickX:null, clickY:null });
    };

    render() {
        const { visible } = this.state;

        return(visible || null) && 
            <div ref={ref => {this.root = ref}} className="contextMenu">
                <div className="contextMenu--option">Share this</div>
                <div className="contextMenu--option">New window</div>
                <div className="contextMenu--option">Visit official site</div>
                <div className="contextMenu--option contextMenu--option__disabled">View full version</div>
                <div className="contextMenu--option">Settings</div>
                <div className="contextMenu--separator" />
                <div className="contextMenu--option">About this app</div>
            </div>
    };
}
