import React from "react";
import ReactDOM from "react-dom";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import styled from 'styled-components';

const Container = styled.div`
height: 100%;
background: #f6f6f6;
`;

const ContextMenuStyle = styled.div`
width: 200px;
height: auto;
box-shadow: 0 0 20px 0 #ccc;

`;

export default class ContextMenuNew extends React.Component {


    handleClick(e, data, id) {
        console.log(data.foo + "id :" + id);
    }

    handleDelete(e, data, id)
     {
        Meteor.call('factories.remove', data._id);
    }
    handleFactoryInsert(e, data, id)
     {

         const factoryData = this.refs.factoryName.value;
         this.refs.factoryName.value = '';

         Meteor.call('factories.insert', factoryData, (err) => { alert(error) });

    }
    handleNodeInsert(e, data, id){
        console.log(this.refs.childNodeCount.value);
    }

    render() {
        return (
            <div >

                <ContextMenuTrigger id={this.props.uniqueId}>
                    <div className="well">{this.props.uniqueId}</div>
                </ContextMenuTrigger>
                <ContextMenuStyle>
                    <ContextMenu id={this.props.uniqueId}>


                        <MenuItem data={{ foo: 'bar1' }} onClick={(event, data) => this.handleClick(event, data, this.props.uniqueId)}>
                            InputText
                            </MenuItem>
                            {this.props.uniqueId === 'root' ?

                        <input ref="factoryName" type = "text" placeholder="Enter factory" ></input>:
                        <input ref="childNodeCount" type = "text" placeholder="ChildNodes count" ></input>}
                        {this.props.uniqueId === 'root' ?
                        <MenuItem data={{ foo: 'bar2' }} onClick={(event, data) =>this.handleFactoryInsert(event, data, this.props.uniqueId)}>
                             Add Factory
                            </MenuItem> :
                            <MenuItem data={{ foo: 'bar2' }} onClick={(event, data) =>this.handleNodeInsert(event, data, this.props.uniqueId)}>
                            Generate
                            </MenuItem>}

         {this.props.uniqueId != 'root' ?
                        <MenuItem divider />:""}
                        {this.props.uniqueId != 'root' ?
                        <MenuItem data={this.props.contextNode} onClick={(event, data) =>this.handleDelete(event, data, this.props.uniqueId)}>
                            Delete
        </MenuItem>:""}
                    </ContextMenu>
                </ContextMenuStyle>

            </div>
        );
    }
}

ContextMenuNew.propTypes = {
    //deleteFactory: PropTypes.func.isRequired,
};
