import React from "react";
import ReactDOM from "react-dom";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import styled from 'styled-components';

const Container = styled.div`
height: 100%;
background: #f6f6f6;
`;

const ContextMenuStyle = styled.div`
width: 200px;
height: auto;
box-shadow: 0 0 20px 0 #ccc;

`;

export default class ContextMenuNew extends React.Component {


    handleClick(e, data, id) {
        console.log(data.foo + "id :" + id);
    }

    handleDelete(e, data, id) {
        Meteor.call('factories.remove', data._id);
    }
    handleInsertFactory(e, data, id) {
        Meteor.call('factories.insert', data._id);
    }

    renderRootMenu() {
        return (
            <div>
                <ContextMenuTrigger id={this.props.uniqueId}>
                    <div className="well">{this.props.uniqueId}</div>
                </ContextMenuTrigger>
                <ContextMenuStyle>
                    <input></input>
                    <MenuItem data={{ foo: 'bar2' }} onClick={(event, data) => this.handleInsertFactory(event, data, this.props.uniqueId)}>
                        Add Factory
                         </MenuItem>
                </ContextMenuStyle>

            </div >

        );
    }

    renderFactoryMenu(){
        return(
            <div>
 <ContextMenuTrigger id={this.props.uniqueId}>
                    <div className="well">{this.props.uniqueId}</div>
                </ContextMenuTrigger>
                <ContextMenuStyle>
                    <ContextMenu id={this.props.uniqueId}>

                        <MenuItem data={{ foo: 'bar1' }} onClick={(event, data) => this.handleClick(event, data, this.props.uniqueId)}>
                            InputText
        </MenuItem>

                        <input ></input>
                        <MenuItem data={{ foo: 'bar2' }} onClick={(event, data) => this.handleInsert(event, data, this.props.uniqueId)}>
                            Generate
        </MenuItem>
                        <MenuItem divider />
                        <MenuItem data={this.props.contextNode} onClick={(event, data) => this.handleDelete(event, data, this.props.uniqueId)}>
                            Delete
        </MenuItem>
                    </ContextMenu>
                </ContextMenuStyle>
            </div>
        );
    }

    render() {
        return (
            <div>
            {this.props.uniqueId == 'root' ? this.renderRootMenu()
                : this.renderFactoryMenu()}
                </div>

        );
    }
}

ContextMenuNew.propTypes = {
    //deleteFactory: PropTypes.func.isRequired,
};
