import React from "react";
import ReactDOM from "react-dom";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import styled from 'styled-components';

const Container = styled.div`
height: 100%;
background: #f6f6f6;
`;

const ContextMenuStyle = styled.div`
width: 200px;
height: auto;
box-shadow: 0 0 20px 0 #ccc;
`;

export default class ContextMenuNew extends React.Component {


    handleClick(e, data, id) {
        //console.log(data.foo + "id :" + id);
    }

    handleDelete(e, data, id) {
        Meteor.call('factories.remove', data._id);
    }
    handleFactoryInsert(e, data, id) {

        const factoryData = this.refs.factoryName.value;
        this.refs.factoryName.value = '';
        const minValue = this.refs.minValue.value;
        this.refs.minValue.value = '';
        const maxValue =  this.refs.maxValue.value;
        this.refs.maxValue.value = '' ;

        Meteor.call('factories.insertFactory', factoryData, data, (err) => { if (err != undefined) alert(err); });

    }
    handleNodeInsert(e, data, id) {
        const nodeCount = this.refs.childNodeCount.value;
        this.refs.childNodeCount.value = '';
        console.log(this.refs.childNodeCount.value);
        Meteor.call('factories.insertChildNodes', data._id, nodeCount, (err) => { if (err != undefined) alert(err); });
    }

    render() {
        return (
            <div >

                <ContextMenuTrigger id={this.props.uniqueId}>
                    <div className="well">{this.props.uniqueId}</div>
                </ContextMenuTrigger>
                <ContextMenuStyle>
                    <ContextMenu id={this.props.uniqueId}>


                        <MenuItem data={this.props.contextNode} onClick={(event, data) => this.handleClick(event, data, this.props.uniqueId)}>
                            InputText
                            </MenuItem>
                        {this.props.uniqueId === 'root' ?

                            <input ref="factoryName" type="text" placeholder="Enter factory" ></input> :
                            <input ref="childNodeCount" type="text" placeholder="ChildNodes count" ></input>}
                        {this.props.uniqueId === 'root' ?
                            <MenuItem data={this.props.contextNode} onClick={(event, data) => this.handleFactoryInsert(event, data, this.props.uniqueId)}>
                                Add Factory
                            </MenuItem> :
                            <MenuItem data={this.props.contextNode} onClick={(event, data) => this.handleNodeInsert(event, data, this.props.uniqueId)}>
                                Generate
                            </MenuItem>}
                            <div>
                            <label>min</label><input ref = "minValue" placeholder = {this.props.uniqueId != 'root' 
                            && this.props.contextNode != undefined ?this.props.contextNode.minValue : ""}></input></div>
                            <label>max</label><input ref= "maxValue" placeholder ={this.props.uniqueId != 'root'&& this.props.contextNode != undefined ?this.props.contextNode.maxValue : ""}></input>

                        {this.props.uniqueId != 'root' ?
                            <MenuItem divider /> : ""}
                        {this.props.uniqueId != 'root' ?
                            <MenuItem data={this.props.contextNode} onClick={(event, data) => this.handleDelete(event, data, this.props.uniqueId)}>
                                Delete
        </MenuItem> : ""}
                    </ContextMenu>
                </ContextMenuStyle>

            </div>
        );
    }
}

ContextMenuNew.propTypes = {
    //deleteFactory: PropTypes.func.isRequired,
};
